const rowsPerPageVice = 10;
let currentPageVice = 1;

function updateViceMayorTable() {
  const rows = document.querySelectorAll(".viceMayorRow");
  const totalPages = Math.ceil(rows.length / rowsPerPageVice);

  rows.forEach((row, index) => {
    row.style.display = (
      index >= (currentPageVice - 1) * rowsPerPageVice &&
      index < currentPageVice * rowsPerPageVice
    ) ? '' : 'none';
  });

  renderVicePagination(totalPages);
}

function changeVicePage(page) {
  const rows = document.querySelectorAll(".viceMayorRow");
  const totalPages = Math.ceil(rows.length / rowsPerPageVice);

  if (page < 1) page = 1;
  if (page > totalPages) page = totalPages;

  currentPageVice = page;
  updateViceMayorTable();
}

function renderVicePagination(totalPages) {
  document.getElementById("vicePageInfo").textContent = This is page ${currentPageVice} out of ${totalPages};
  const container = document.getElementById("vicePagination");
  container.innerHTML = "";

  const prevBtn = document.createElement("button");
  prevBtn.textContent = "← Prev";
  prevBtn.className = currentPageVice === 1 ? "disabled" : "";
  prevBtn.disabled = currentPageVice === 1;
  prevBtn.onclick = () => changeVicePage(currentPageVice - 1);
  container.appendChild(prevBtn);

  const maxPagesToShow = 7;
  let startPage = 1;
  let endPage = totalPages;

  if (totalPages > maxPagesToShow) {
    if (currentPageVice <= 4) {
      endPage = 5;
    } else if (currentPageVice >= totalPages - 3) {
      startPage = totalPages - 4;
    } else {
      startPage = currentPageVice - 2;
      endPage = currentPageVice + 2;
    }
  }

  if (startPage > 1) {
    addVicePageButton(container, 1);
    if (startPage > 2) {
      addViceEllipsis(container);
    }
  }

  for (let i = startPage; i <= endPage; i++) {
    addVicePageButton(container, i);
  }

  if (endPage < totalPages) {
    if (endPage < totalPages - 1) {
      addViceEllipsis(container);
    }
    addVicePageButton(container, totalPages);
  }

  const nextBtn = document.createElement("button");
  nextBtn.textContent = "Next →";
  nextBtn.className = currentPageVice === totalPages ? "disabled" : "";
  nextBtn.disabled = currentPageVice === totalPages;
  nextBtn.onclick = () => changeVicePage(currentPageVice + 1);
  container.appendChild(nextBtn);
}

